package com.library.app.controller;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ResponseBody;

import com.library.app.model.Books;
import com.library.app.repository.BookRepository;

@Controller // This means that this class is a Controller
public class DashboardController {
    @Autowired // This means to get the bean called userRepository
    // Which is auto-generated by Spring, we will use it to handle the data
    private BookRepository bookRepository;

    @GetMapping(path = "/dashboard/transactions/loan")
    public @ResponseBody Iterable<Books> getTransactionLoan() {
        return bookRepository.findAll();
    }

    @GetMapping(path = "/dashboard/transactions/return")
    public @ResponseBody Iterable<Books> getTransactionReturn() {
        return bookRepository.findAll();
    }

    @GetMapping(path = "/dashboard/transactions/most-loan")
    public @ResponseBody Iterable<Books> getTransactionMostLoan() {
        return bookRepository.findAll();
    }

    @GetMapping(path = "/dashboard/books/total")
    public @ResponseBody Iterable<Books> getBookTotal() {
        return bookRepository.findAll();
    }

    @GetMapping(path = "/dashboard/books/available")
    public @ResponseBody Iterable<Books> getBookAvailable() {
        return bookRepository.findAll();
    }
}
